          var cusList = this.invoiceList;
          ids_array.forEach(function (id) {
            cusList = cusList.filter(function (orders) {
              return orders.invoiceId != id;
            });
          });
          this.invoiceList = cusList;
          document.getElementById("checkAll").checked = false;
