      // TODO: ochish yoki modal ko'rsatish
    };
    onMounted(() => {
      getEnCoder();
    });
    const __returned__ = {
      title,
      items,
      userModalRef,
      enCoders,
      loading,
      getEnCoder,
      editDefinition,
      ref,
      onMounted,
      Layout,
      PageHeader,
      get getEnCoder_API() {
        return getEnCoder_API;
      },
      get Plus() {
        return Plus;
      },
      get Edit() {
        return Edit;
      },
      EnCoder
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
