  computed: {
    displayedPosts() {
      return this.paginate(this.application);
    },
    resultQuery() {
      if (this.searchQuery) {
        const search = this.searchQuery.toLowerCase();
        return this.displayedPosts.filter(data => {
          return data.name.toLowerCase().includes(search) || data.designation.toLowerCase().includes(search) || data.type.toLowerCase().includes(search) || data.contacts.toLowerCase().includes(search) || data.status.toLowerCase().includes(search);
        });
      } else if (this.status !== null || this.value !== null) {
        return this.displayedPosts.filter(data => {
          if (this.status != null && this.value != null) {
            if (this.status === data.status) {
              if (this.value === data.type) {
                return data;
              } else if (this.value == 'All') {
                return data;
              }
            } else if (this.value == 'All' && this.status == 'All') {
              return data;
            } else if (this.status == 'All') {
              if (this.value === data.type) {
                return data;
              }
            }
          } else if (this.status === data.status) {
            return data;
          } else if (this.value === data.type) {
            return data;
          } else if (this.status == 'All' || this.value == 'All') {
            return data;
          } else {
            return null;
          }
        });
      } else {
        return this.displayedPosts;
      }
    }
