    resultQuery() {
      if (this.searchQuery) {
        const search = this.searchQuery.toLowerCase();
        return this.displayedPosts.filter(data => {
          return data.orderId.toLowerCase().includes(search) || data.customer.toLowerCase().includes(search) || data.product.toLowerCase().includes(search) || data.orderDate.toLowerCase().includes(search) || data.amount.toString().toLowerCase().includes(search) || data.payment.toLowerCase().includes(search) || data.status.toLowerCase().includes(search);
        });
      } else if (this.date !== null) {
        if (this.date !== null) {
          var date1 = this.date.split(" to ")[0];
          var date2 = this.date.split(" to ")[1];
        }
        return this.displayedPosts.filter(data => {
          if (new Date(data.orderDate.slice(0, 12)) >= new Date(date1) && new Date(data.orderDate.slice(0, 12)) <= new Date(date2)) {
            return data;
          } else {
            return null;
          }
        });
      } else if (this.status !== null || this.value !== null) {
        return this.displayedPosts.filter(data => {
          if (this.status != null && this.value != null) {
            if (this.status === data.status) {
              if (this.value === data.payment) {
                return data;
              } else if (this.value == 'All') {
                return data;
              }
            } else if (this.value == 'All' && this.status == 'All') {
