  computed: {
    displayedPosts() {
      return this.paginate(this.contactsList);
    },
    resultQuery() {
      if (this.searchQuery) {
        const search = this.searchQuery.toLowerCase();
        return this.displayedPosts.filter(data => {
          return data.company.toLowerCase().includes(search) || data.designation.toLowerCase().includes(search) || data.email.toLowerCase().includes(search) || data.image_src.toLowerCase().includes(search) || data.last_contacted.toLowerCase().includes(search) || data.lead_score.toString().toLowerCase().includes(search) || data.name.toLowerCase().includes(search) || data.phone.toLowerCase().includes(search);
