  computed: {
    resultQuery() {
      if (this.searchQuery) {
        const search = this.searchQuery.toLowerCase();
        return this.todoList.filter(data => {
          return data.priority.toLowerCase().includes(search) || data.id.toLowerCase().includes(search) || data.status.toLowerCase().includes(search) || data.todo.toLowerCase().includes(search) || data.dueDate.toLowerCase().includes(search);
        });
      } else if (this.status !== null) {
        return this.todoList.filter(data => {
          if (data.status === this.status || this.status == 'All Tasks') {
            return data;
          } else {
            return null;
          }
        });
      } else {
        return this.todoList;
      }
    }
