        showCancelButton: true,
        cancelButtonColor: "#f46a6a",
        confirmButtonColor: "#34c38f",
        confirmButtonText: "Yes, delete it!"
      }).then(result => {
        if (result.value) {
          this.todoList.splice(this.todoList.indexOf(event), 1);
