import { ref } from "vue";
import Layout from "@/layouts/main.vue";
import PageHeader from "@/components/page-header.vue";
import axios from "axios";

// Header sarlavha va breadcrumbs

export default {
  __name: 'payment',
  setup(__props, {
    expose
  }) {
    expose();
    const title = "Оплата";
    const items = [{
      text: "To‘lov",
      href: "/"
    }, {
      text: "YooKassa",
      active: true
    }];

    // To‘lovni boshlash
    const initPayment = async () => {
      try {
        const {
          data
        } = await axios.post("https://v.zesu.ru/payment/create/");
        const checkout = new window.YooMoneyCheckoutWidget({
          confirmation_token: data.confirmation_token,
          return_url: "https://your-site.com/success",
          error_callback: function (error) {
            console.log("To‘lov xatosi:", error);
          }
        });
        checkout.render("payment-form");
      } catch (err) {
        console.error("To‘lov yaratib bo‘lmadi", err);
      }
    };
    const __returned__ = {
      title,
      items,
      initPayment,
      ref,
      Layout,
      PageHeader,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
