    displayedPosts() {
      return this.paginate(this.companies);
    },
    resultQuery() {
      if (this.searchQuery) {
        const search = this.searchQuery.toLowerCase();
        return this.displayedPosts.filter(data => {
          return data.contact_email.toLowerCase().includes(search) || data.employee.toLowerCase().includes(search) || data.owner.toLowerCase().includes(search) || data.industry_type.toLowerCase().includes(search) || data.website.toLowerCase().includes(search) || data.star_value.toString().toLowerCase().includes(search) || data.name.toLowerCase().includes(search) || data.location.toLowerCase().includes(search);
